var cbpHorizontalMenu=(function(){
	var b = $("#cbp-hrmenu > div > ul > li")
	 ,g = b.children("a")
	 ,c = $("body")
	 ,d = -1;

	function f(){
		g.on("click",a);
		b.on("click",function(h){
			h.stopPropagation()
		})
	}
	function a(j){
		if(d!==-1){
			b.eq(d).removeClass("open")
		}
		var i=$(j.currentTarget).parent("li")
		   ,h=i.index();

		if(d===h){
			i.removeClass("open");
			d=-1
		} else {
			if (i.hasClass("has-child")) {
				i.addClass("open");
				d=h;
				c.off("click").on("click",e)
			} else {
				return true
			}
		}
		return false
	}
	function e(h){
		b.eq(d).removeClass("open");
		d=-1
	}
	return {init:f}
})();